#!/usr/bin/env python
import xmlrpclib
import time
import sys

proxy = xmlrpclib.ServerProxy("http://&Kickstart_PrivateAddress;:8000/")

def put_token(key,token):
	proxy.write_token(key,token)

def get_token(key):
	try:
		token = proxy.read_token(key)
		return token
	except:
		return 255

def clear_token():
	proxy.clear_token()

action = sys.argv[1]
if action == 'clear':
	clear_token()
elif action == 'put':
	key = sys.argv[2]
	token = sys.argv[3]
	put_token(key,token)
elif action == 'get':
	key = sys.argv[2]
	get_token(key)
	ret = get_token(key)
	while ret == 255:
		time.sleep(10)
		ret = get_token(key)
	print ret
